mapper.select:
SELECT od.psize, od.order_id, od.amount, od.price, od.state, delivery_no, od.product_detail_id
FROM order_detail od
WHERE od.order_id = #{value}

mapper.update:
UPDATE order_detail
SET state = #{state}
WHERE product_detail_id = #{productDetailId} AND order_id = #{orderId} AND psize = #{psize}
