mapper.select:
SELECT od.product_name, od.brand_name, od.psize, od.order_id, od.amount, od.price, od.delivery_state, delivery_no
FROM order_detail od
WHERE od.order_id = #{value}

mapper.update:
UPDATE order_detail
SET delivery_state = #{deliveryState}
WHERE product_detail_id = #{productDetailId} AND order_id = #{orderId} AND psize = #{psize}
