mapper.select:
SELECT member_id, order_date, state, request, zip_code, address1, address2, tel, tel, name, coupon_id
FROM morder
WHERE order_id = #{value}

SELECT order_date, state, request, zip_code, address1, address2, tel, tel, name, coupon_id
FROM morder
